const NAVBAR_HEIGHT=82;function toggleMenu(){document.querySelector("nav > ul").classList.toggle("visible"),document.querySelector("nav").classList.add("scroll")}function toggleNavbarScrollClass(){const e=document.querySelector("nav");window.scrollY>=NAVBAR_HEIGHT?e.classList.add("scroll"):e.classList.remove("scroll")}function setActiveClassOnMenuItems(){const e=Math.ceil(window.scrollY);if(e>=window.scrollMaxY){const e=document.querySelector(`a[href='#contact']`);if(!e)return;e.parentElement.classList.add("active"),document.querySelectorAll("nav ul li a:not([href='#contact'])").forEach(e=>{e.parentElement.classList.remove("active")});return}document.querySelectorAll("nav ul li a").forEach(t=>{const n=document.querySelector(t.hash);if(n){const s=n.offsetParent,o=parseInt(n.dataset.extraSpace||0,10)+NAVBAR_HEIGHT,i=e+o,a=s.offsetTop+s.offsetHeight-o;if(s.offsetTop<=i&&a>e)return t.parentElement.classList.add("active");t.parentElement.classList.remove("active")}})}function hideMobileMenu(){document.querySelector("nav > ul").classList.remove("visible")}window.addEventListener("scroll",toggleNavbarScrollClass),window.addEventListener("scroll",setActiveClassOnMenuItems),window.addEventListener("scroll",hideMobileMenu),document.getElementById("toggleMenuButton").addEventListener("click",toggleMenu)