const OFFSET=150;function revealDecorationElements(){document.querySelectorAll(".title, .subtitle, .icon, .toggle").forEach(e=>{const t=window.innerHeight,n=e.getBoundingClientRect().top,s=OFFSET;n<t-s?e.classList.add("active"):e.classList.contains("toggle")&&e.classList.remove("active")})}window.addEventListener("scroll",revealDecorationElements),revealDecorationElements()